import React from 'react';
import styled from 'styled-components';

const SignModal = styled.div`
  background: rgba(0, 0, 0, 0.25);
  position: fixed;
  left: 0;
  top: 0;
  height: 100%;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const modal = (props) => {
  const onClose = (e) => {
    props.onClose && props.onClose(e);
  };
  if (!props.show) {
    return null;
  }
  return (
    <div>
      <SignModal>
        <div className="content">
          <h3>My Modal</h3>
          <p>{props.children}</p>
          <button
            onClick={(e) => {
              onClose(e);
            }}
          >
            Close
          </button>
        </div>
      </SignModal>
      <style jsx>{`
        .MyModal {
          background: rgba(0, 0, 0, 0.25);
          position: fixed;
          left: 0;
          top: 0;
          height: 100%;
          width: 100%;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .MyModal .content {
          background: white;
          padding: 1rem;
          width: 400px;
          height: auto;
        }
      `}</style>
    </div>
  );
};

export default modal;
